import React, { useRef, useState, useEffect } from 'react';
import io from 'socket.io-client';

const SIGNALING_SERVER_URL = 'http://192.168.98.141:5001';
const ROOM_ID = 'test-room';

const LiveStream = () => {
  const [isStreamer, setIsStreamer] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [connected, setConnected] = useState(false);
  const [status, setStatus] = useState('Waiting for connection...');
  const [localStream, setLocalStream] = useState(null);
  const [remoteStream, setRemoteStream] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [chunks, setChunks] = useState([]);

  const localVideo = useRef();
  const remoteVideo = useRef();
  const pc = useRef(null);
  const socket = useRef(null);

  const startWebRTC = async (streamer, shouldRecord = false) => {
    setIsStreamer(streamer);
    setIsRecording(shouldRecord);
    setStatus('Connecting...');

    socket.current = io(SIGNALING_SERVER_URL);
    pc.current = new window.RTCPeerConnection();

    socket.current.emit('join', ROOM_ID);

    socket.current.on('new-peer', async () => {
      if (streamer) {
        setStatus('Creating offer...');
        const offer = await pc.current.createOffer();
        await pc.current.setLocalDescription(offer);
        socket.current.emit('signal', { room: ROOM_ID, data: { type: 'offer', sdp: offer.sdp } });
      }
    });

    socket.current.on('signal', async (data) => {
      if (data.type === 'offer' && !streamer) {
        setStatus('Receiving offer...');
        await pc.current.setRemoteDescription({ type: 'offer', sdp: data.sdp });
        const answer = await pc.current.createAnswer();
        await pc.current.setLocalDescription(answer);
        socket.current.emit('signal', { room: ROOM_ID, data: { type: 'answer', sdp: answer.sdp } });
      } else if (data.type === 'answer' && streamer) {
        setStatus('Receiving answer...');
        await pc.current.setRemoteDescription({ type: 'answer', sdp: data.sdp });
      } else if (data.type === 'candidate') {
        try {
          await pc.current.addIceCandidate(data.candidate);
        } catch (e) {}
      }
    });

    pc.current.onicecandidate = (event) => {
      if (event.candidate) {
        socket.current.emit('signal', { room: ROOM_ID, data: { type: 'candidate', candidate: event.candidate } });
      }
    };

    pc.current.ontrack = (event) => {
      setRemoteStream(event.streams[0]);
      setStatus('Connected! Receiving stream...');
    };

    pc.current.oniceconnectionstatechange = () => {
      if (pc.current.iceConnectionState === 'connected') {
        setStatus('WebRTC connected!');
        setConnected(true);
      } else if (pc.current.iceConnectionState === 'disconnected') {
        setStatus('Connection lost');
        setConnected(false);
      }
    };

    if (streamer) {
      try {
        setStatus('Accessing camera/microphone...');
        const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        setLocalStream(stream);
        localVideo.current.srcObject = stream;
        stream.getTracks().forEach((track) => pc.current.addTrack(track, stream));

        if (shouldRecord) {
          startRecording(stream);
        }
      } catch (err) {
        setStatus('Error: Could not access camera/microphone');
        console.error('getUserMedia error:', err);
      }
    }
  };

  const startRecording = (stream) => {
    const recorder = new window.MediaRecorder(stream);
    setMediaRecorder(recorder);
    setChunks([]);

    recorder.ondataavailable = (e) => {
      if (e.data.size > 0) {
        setChunks(prev => [...prev, e.data]);
      }
    };

    recorder.onstop = async () => {
      const blob = new Blob(chunks, { type: 'video/webm' });
      const url = URL.createObjectURL(blob);

      // Upload to backend
      const formData = new FormData();
      formData.append('file', blob, 'live-recording.webm');
      try {
        await fetch('http://192.168.98.141:5001/api/recordings', { 
          method: 'POST', 
          body: formData 
        });
        setStatus('Recording saved!');
      } catch (err) {
        setStatus('Error saving recording');
      }
    };

    recorder.start();
    setStatus('Live streaming and recording...');
  };

  const stopStreaming = () => {
    if (mediaRecorder && isRecording) {
      mediaRecorder.stop();
    }
    if (localStream) {
      localStream.getTracks().forEach(track => track.stop());
    }
    if (socket.current) {
      socket.current.disconnect();
    }
    if (pc.current) {
      pc.current.close();
    }
    setLocalStream(null);
    setRemoteStream(null);
    setIsStreamer(false);
    setIsRecording(false);
    setConnected(false);
    setStatus('Disconnected');
  };

  return (
    <div className="max-w-4xl mx-auto mt-10 bg-white rounded-lg shadow p-6">
      <h2 className="text-2xl font-bold mb-4 text-blue-700">Live Stream</h2>

      <div className="mb-4 p-3 bg-gray-100 rounded">
        <p className="text-sm text-gray-700">Status: {status}</p>
      </div>

      {!connected && !isStreamer && (
        <div className="flex gap-4 mb-6">
          <button
            onClick={() => startWebRTC(true, false)}
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded shadow"
          >
            Start Live Stream
          </button>
          <button
            onClick={() => startWebRTC(true, true)}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded shadow"
          >
            Live Stream + Record
          </button>
          <button
            onClick={() => startWebRTC(false, false)}
            className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded shadow"
          >
            View Stream
          </button>
        </div>
      )}

      {isStreamer && (
        <button
          onClick={stopStreaming}
          className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded shadow mb-4"
        >
          Stop Streaming
        </button>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {isStreamer && (
          <div>
            <h3 className="font-semibold mb-2">Your Stream (Local)</h3>
            <video
              ref={localVideo}
              autoPlay
              playsInline
              muted
              className="rounded-lg shadow-lg border-2 border-blue-200 w-full h-64 bg-black"
            />
          </div>
        )}

        <div>
          <h3 className="font-semibold mb-2">Remote Stream</h3>
          <video
            ref={remoteVideo}
            autoPlay
            playsInline
            className="rounded-lg shadow-lg border-2 border-blue-200 w-full h-64 bg-black"
          />
          {!remoteStream && !isStreamer && (
            <div className="flex items-center justify-center h-64 bg-gray-100 rounded-lg">
              <p className="text-gray-500">Waiting for stream...</p>
            </div>
          )}
        </div>
      </div>

      {isRecording && (
        <div className="mt-4 p-3 bg-red-100 rounded">
          <p className="text-red-700 text-sm">ðŸ”´ Recording in progress...</p>
        </div>
      )}
    </div>
  );
};

export default LiveStream;
