import React, { useRef, useState } from 'react';

const API_URL = 'http://localhost:5001/api/recordings';

const Record = () => {
  const [recording, setRecording] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const videoRef = useRef();
  const chunks = useRef([]);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
    setMediaStream(stream);
    videoRef.current.srcObject = stream;
    const recorder = new window.MediaRecorder(stream);
    setMediaRecorder(recorder);
    chunks.current = [];
    recorder.ondataavailable = (e) => {
      if (e.data.size > 0) chunks.current.push(e.data);
    };
    recorder.onstop = async () => {
      const blob = new Blob(chunks.current, { type: 'video/webm' });
      setPreviewUrl(URL.createObjectURL(blob));
      // Upload to backend
      const formData = new FormData();
      formData.append('file', blob, 'recording.webm');
      await fetch(API_URL, { method: 'POST', body: formData });
    };
    recorder.start();
    setRecording(true);
  };

  const stopRecording = () => {
    mediaRecorder.stop();
    mediaStream.getTracks().forEach((track) => track.stop());
    setRecording(false);
  };

  return (
    <div className="flex flex-col items-center mt-8">
      <h2 className="text-2xl font-bold mb-4">Record Audio/Video</h2>
      <video ref={videoRef} autoPlay playsInline muted className="rounded shadow-lg border border-gray-300 w-full max-w-xl h-64 bg-black mb-4" />
      <div className="flex gap-4">
        {!recording ? (
          <button onClick={startRecording} className="px-4 py-2 bg-green-600 text-white rounded">Start Recording</button>
        ) : (
          <button onClick={stopRecording} className="px-4 py-2 bg-red-600 text-white rounded">Stop Recording</button>
        )}
      </div>
      {previewUrl && (
        <div className="mt-4">
          <h3 className="font-semibold mb-2">Preview</h3>
          <video src={previewUrl} controls className="rounded border w-full max-w-xl h-64 bg-black" />
        </div>
      )}
    </div>
  );
};

export default Record;
