import React, { useRef, useState } from 'react';
import toast from 'react-hot-toast';

const API_URL = 'http://192.168.98.141:5001/api/recordings';

const Record = () => {
  const [recording, setRecording] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [cameras, setCameras] = useState([]);
  const [currentCameraIndex, setCurrentCameraIndex] = useState(0);
  const videoRef = useRef();
  const chunks = useRef([]);

  // Get available cameras
  const getCameras = async () => {
    try {
      const devices = await navigator.mediaDevices.enumerateDevices();
      const videoDevices = devices.filter(device => device.kind === 'videoinput');
      setCameras(videoDevices);
      return videoDevices;
    } catch (err) {
      console.error('Error getting cameras:', err);
      return [];
    }
  };

  // Switch camera
  const switchCamera = async () => {
    if (cameras.length < 2) {
      toast.error('Only one camera available');
      return;
    }

    const newIndex = (currentCameraIndex + 1) % cameras.length;
    setCurrentCameraIndex(newIndex);

    if (mediaStream) {
      // Stop current stream
      mediaStream.getTracks().forEach(track => track.stop());
    }

    try {
      toast.loading('Switching camera...');
      const newStream = await navigator.mediaDevices.getUserMedia({
        video: { deviceId: { exact: cameras[newIndex].deviceId } },
        audio: true
      });

      setMediaStream(newStream);
      videoRef.current.srcObject = newStream;

      // Update MediaRecorder if recording
      if (recording && mediaRecorder) {
        mediaRecorder.stop();
        const newRecorder = new window.MediaRecorder(newStream);
        setMediaRecorder(newRecorder);
        chunks.current = [];

        newRecorder.ondataavailable = (e) => {
          if (e.data.size > 0) chunks.current.push(e.data);
        };

        newRecorder.onstop = async () => {
          const blob = new Blob(chunks.current, { type: 'video/webm' });
          setPreviewUrl(URL.createObjectURL(blob));
          // Upload to backend
          const formData = new FormData();
          formData.append('file', blob, 'recording.webm');
          try {
            await fetch(API_URL, { method: 'POST', body: formData });
            toast.success('Upload successful!');
          } catch {
            toast.error('Upload failed!');
          }
        };

        newRecorder.start();
      }

      toast.success('Camera switched!');
    } catch (err) {
      toast.error('Error switching camera');
      console.error('Camera switch error:', err);
    }
  };

  const startRecording = async () => {
    try {
      await getCameras(); // Get available cameras first
      const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      setMediaStream(stream);
      videoRef.current.srcObject = stream;
      const recorder = new window.MediaRecorder(stream);
      setMediaRecorder(recorder);
      chunks.current = [];
      recorder.ondataavailable = (e) => {
        if (e.data.size > 0) chunks.current.push(e.data);
      };
      recorder.onstop = async () => {
        const blob = new Blob(chunks.current, { type: 'video/webm' });
        setPreviewUrl(URL.createObjectURL(blob));
        // Upload to backend
        const formData = new FormData();
        formData.append('file', blob, 'recording.webm');
        try {
          await fetch(API_URL, { method: 'POST', body: formData });
          toast.success('Upload successful!');
        } catch {
          toast.error('Upload failed!');
        }
      };
      recorder.start();
      setRecording(true);
    } catch (err) {
      toast.error('Could not access camera/mic.');
    }
  };

  const stopRecording = () => {
    mediaRecorder.stop();
    mediaStream.getTracks().forEach((track) => track.stop());
    setRecording(false);
  };

  return (
    <div className="max-w-xl mx-auto mt-10 bg-white rounded-lg shadow p-6">
      <h2 className="text-2xl font-bold mb-4 text-blue-700">Record Audio/Video</h2>

      {/* Camera Info */}
      {cameras.length > 1 && (
        <div className="mb-4 p-3 bg-gray-100 rounded">
          <p className="text-sm text-gray-700">
            Available cameras: {cameras.length} | Current: {cameras[currentCameraIndex]?.label || 'Camera ' + (currentCameraIndex + 1)}
          </p>
        </div>
      )}

      <video
        ref={videoRef}
        autoPlay
        playsInline
        muted
        className="rounded border-2 border-blue-200 w-full h-64 bg-black mb-4 shadow"
      />

      <div className="flex gap-4 mb-4">
        {!recording ? (
          <button
            onClick={startRecording}
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded shadow"
          >
            Start Recording
          </button>
        ) : (
          <button
            onClick={stopRecording}
            className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded shadow"
          >
            Stop Recording
          </button>
        )}

        {cameras.length > 1 && (
          <button
            onClick={switchCamera}
            className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded shadow"
          >
            Switch Camera
          </button>
        )}
      </div>

      {loading && (
        <div className="flex justify-center items-center h-10">
          <svg className="animate-spin h-6 w-6 text-blue-600" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none"/>
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8z"/>
          </svg>
          <span className="ml-2 text-blue-700">Uploading...</span>
        </div>
      )}
      {previewUrl && (
        <div className="mt-4">
          <h3 className="font-semibold mb-2">Preview</h3>
          <video src={previewUrl} controls className="rounded border w-full h-64 bg-black shadow" />
        </div>
      )}
    </div>
  );
};

export default Record;
