import React, { useEffect, useState } from 'react';

const API_URL = 'http://192.168.98.141:5001/api/recordings';

const Playback = () => {
  const [recordings, setRecordings] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => setRecordings(data));
  }, []);

  return (
    <div className="flex flex-col items-center mt-8">
      <h2 className="text-2xl font-bold mb-4">Playback Recordings</h2>
      <ul className="mb-4 w-full max-w-xl">
        {recordings.map((rec) => (
          <li key={rec._id} className="flex justify-between items-center border-b py-2">
            <span>{rec.filename || rec._id}</span>
            <button onClick={() => setSelected(rec._id)} className="px-2 py-1 bg-blue-600 text-white rounded">Play</button>
          </li>
        ))}
      </ul>
      {selected && (
        <video
          src={`${API_URL}/${selected}`}
          controls
          autoPlay
          className="rounded border w-full max-w-xl h-64 bg-black"
        />
      )}
    </div>
  );
};

export default Playback;
